/**
 * The position of an axis title.
 *
 * @example
 * ```jsx
 *  <Chart>
 *    <ChartValueAxis>
 *      <ChartValueAxisItem :title="{ text: 'Title', position: 'top' }"/>
 *    </ChartValueAxis>
 *  </Chart>
 *
 * ```
 */
export declare type AxisTitlePosition = 'top' | 'bottom' | 'left' | 'right' | 'center';
