import { BaseUnit } from './base-unit';
/**
 * The `baseUnit` type of the category axis.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartCategoryAxis,
 *  ChartCategoryAxisItem,
 *  ChartSeries,
 *  ChartSeriesItem
 * } from '@progress/kendo-vue-charts';
 *
 * <template>
 *  <Chart>
 *    <ChartCategoryAxis>
 *     <ChartCategoryAxisItem :base-unit="baseUnit" />
 *    </ChartCategoryAxis>
 *    <ChartSeries>
 *      <ChartSeriesItem :category-field="category" :data-items="dataItems" />
 *    </ChartSeries>
 *  </Chart>
 * </template>
 * <script>
 * export default {
 *   components: {
 *       Chart,
 *       ChartCategoryAxis,
 *       ChartCategoryAxisItem,
 *       ChartSeries,
 *       ChartSeriesItem
 *   },
 *   data () {
 *       return {
 *          baseUnit: "months",
 *          dataItems: [
 *              { category: new Date(2000, 0, 1), value: 1 },
 *              { category: new Date(2001, 0, 1), value: 1}
 *          ]
 *       };
 *   }
 * }
 * </script>
 * ```
 */
export declare type CategoryBaseUnit = BaseUnit | 'auto' | 'fit';
