/**
 * Specifies the position of a note.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartValueAxis,
 *  ChartValueAxisItem
 * } from '@progress/kendo-жуе-charts';
 *
 * <template>
 *  <Chart>
 *    <ChartValueAxis>
 *      <ChartValueAxisItem :notes="note" />
 *    </ChartValueAxis>
 *  </Chart>
 *  </template>
 * <script>
 * export default {
 *   components: {
 *      Chart,
 *      ChartValueAxis,
 *      ChartValueAxisItem
 *   },
 *   computed: {
 *      notes(){
 *          return { dataItems: this.dataItems, position: this.position }
 *      }
 *   },
 *   data () {
 *       return {
 *             dataItems: [{value: 1}],
 *             position: "right"
 *       };
 *   }
 * }
 * </script>
 * ```
 */
export declare type NotePosition = 'top' | 'bottom' | 'left' | 'right';
