/**
 * The `baseUnit` type of an X or Y axis.
 *
 * @example
 * ```jsx
 *  <Chart>
 *    <ChartXAxis>
 *     <ChartXAxisItem :base-unit="baseUnit" />
 *    </ChartXAxis>
 *    <ChartSeries>
 *      <ChartSeriesItem :type="'scatter'" :data-items="data" />
 *    </ChartSeries>
 *  </Chart>
 * ```
 */
export declare type BaseUnit = 'milliseconds' | 'seconds' | 'minutes' | 'hours' | 'days' | 'weeks' | 'months' | 'years';
