/**
 * The dash line type.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartSeries,
 *  ChartSeriesItem
 * } from '@progress/kendo-vue-charts';
 *
 * <template>
 *  <Chart>
 *    <ChartSeries>
 *      <ChartSeriesItem :type="'line'" :data-items="dataItems" :dash-type="'dot'" />
 *    </ChartSeries>
 *  </Chart>
 *  </template>
 * <script>
 * export default {
 *   components: {
 *      Chart,
 *      ChartSeries,
 *      ChartSeriesItem
 *   },
 *   data () {
 *       return {
 *          data: [1, 2, 3]
 *       };
 *   }
 * }
 * </script>
 * ```
 */
export declare type DashType = 'dash' | 'dashDot' | 'dot' | 'longDash' | 'longDashDot' | 'longDashDotDot' | 'solid';
