/**
 * The line style of the series.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartSeries,
 *  ChartSeriesItem
 * } from '@progress/kendo-vue-charts';
 *
 * <template>
 *  <Chart>
 *    <ChartSeries>
 *      <ChartSeriesItem :type="'line'" :data-items="dataItems" :item-style="'smooth'" />
 *    </ChartSeries>
 *  </Chart>
 *  </template>
 * <script>
 * export default {
 *   components: {
 *      Chart,
 *      ChartSeries,
 *      ChartSeriesItem
 *   },
 *   data () {
 *       return {
 *          dataItems: [1, 2, 3]
 *       };
 *   }
 * }
 * </script>
 * ```
 */
export declare type LineStyle = 'normal' | 'step' | 'smooth';
