/**
 * Specifies the marker type.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartSeries,
 *  ChartSeriesItem
 * } from '@progress/kendo-vue-charts';
 *
 * <template>
 *  <Chart>
 *    <ChartSeries>
 *      <ChartSeriesItem :type="'line'" :data-items="dataItems" :markers="markerType" />
 *    </ChartSeries>
 *  </Chart>
 *  </template>
 * <script>
 * export default {
 *   components: {
 *      Chart,
 *      ChartSeries,
 *      ChartSeriesItem
 *   },
 *   data () {
 *       return {
 *          dataItems: [1, 2, 3],
 *          markerType: { type: "cross" }
 *       };
 *   }
 * }
 * </script>
 * ```
 */
export declare type MarkerType = 'square' | 'circle' | 'triangle' | 'cross';
