/**
 * The position of a note label.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartValueAxis,
 *  ChartValueAxisItem
 * } from '@progress/kendo-vue-charts';
 *
 * <template>
 *  <Chart>
 *    <ChartValueAxis>
 *      <ChartValueAxisItem :notes="notes" />
 *    </ChartValueAxis>
 *  </Chart>
 *  </template>
 * <script>
 * export default {
 *   components: {
 *      Chart,
 *      ChartValueAxis,
 *      ChartValueAxisItem
 *   },
 *   computed: {
 *      notes(){
 *          return { dataItems: this.dataItems, label: { position: this.position } }
 *      }
 *   },
 *   data () {
 *       return {
 *           dataItems: [{value: 1, label: { text: 'Foo' }}],
 *           position: "outside"
 *       };
 *   }
 * }
 * </script>
 * ```
 */
export declare type NoteLabelPosition = 'inside' | 'outside';
